var data = $(".input").val();
var count = true;
$.ajax({
    type: "post",
    url: "../../php/information.php",
    data: data,
    dataType: "json",
    success: function(response) {
        function infor_search() {
            console.log(3);
            var str = $(".search1>.input").val();
            var put = "";
            count = false;
            if (count == false) {
                $("#loadmore").css("display", "none");
            }
            for (var j in response) {
                if (response[j].s_decrition.indexOf(str) != -1 && str) {
                    put += `
                    <div class="item" data-order=${response[j].s_id}>
                    <div class="container-fluid box">
                        <a href="#" class="sign-a">
                        <img src="${response[j].s_url}">
                        </a>
                        <div class="date">
                            <span class="day">
                            <p class="ri">${response[j].s_day}</p>
                            <p class="month">${response[j].s_month}</p>
                        </span>
                            <div class="xq">
                                <p class="dec">
                                    <a href="#">
                                    ${response[j].s_decrition}
                                </a>
                                </p>
                                <p class="vid">
                                    <a href="#">
                                    ${response[j].s_vi}                                                                          
                                </a>
                                </p>
                            </div>
                            <a href="#" class="details">
                                <span class="detail-co">Details</span>
                                <span class="dec-icon"></span>
                            </a>
                        </div>
                    </div>
                </div>`;
                }
                $(".my-gallery-container").html(put);
                $.getScript("../../js/mp.mansory.min.js");
                $.getScript("../../js/pubuliu.js");
            }
        }
        // enter键搜索
        $(".search1 .input").on("keyup", function(event) {
            console.log(1);
            if (event.keyCode == 13) {
                infor_search();
            }
        });
        $(".iconfont1").on("click", infor_search);
        // 加载更多
        $(".more-load").on("click", function() {
            $("#loadmore").css("background", "#333").addClass("animated bounceOutLeft").animate({
                "height": "40px",
                "width": "80px",
                "top": "-150px",
                "left": "-200px",
                "display": "none"
            }, 500)
            setTimeout(function() {
                count = false;
                var output = "";
                if (count == false) {
                    $("#loadmore");
                    $(".news").css("paddingBottom", "0");
                }
                for (var i = 0; i < response.length; i++) {
                    // console.log(i);
                    if (i > 8) {
                        output = `
                            <div class="item" data-order=${response[i].s_id}>
                            <div class="container-fluid box">
                                <a href="#" class="sign-a">
                                <img src="${response[i].s_url}">
                                </a>
                                <div class="date">
                                    <span class="day">
                                    <p class="ri">${response[i].s_day}</p>
                                    <p class="month">${response[i].s_month}</p>
                                </span>
                                    <div class="xq">
                                        <p class="dec">
                                            <a href="#">
                                            ${response[i].s_decrition}
                                        </a>
                                        </p>
                                        <p class="vid">
                                            <a href="#">
                                            ${response[i].s_vi}                                                                          
                                        </a>
                                        </p>
                                    </div>
                                    <a href="#" class="details">
                                        <span class="detail-co">Details</span>
                                        <span class="dec-icon"></span>
                                    </a>
                                </div>
                            </div>
                        </div>`;
                        var len = $(".padding").length;
                        console.log(len);
                        if (len == 3) {
                            if (i % 3 == 0) {
                                // console.log(1);
                                // console.log($(".padding:first"));
                                $(".padding:first").append(output);
                            } else if (i % 3 == 1) {
                                // console.log(2);
                                // console.log($(".padding:eq(1)"));
                                $(".padding:eq(1)").append(output);
                            } else {
                                // console.log(3);
                                // console.log($(".padding:eq(2)"));
                                $(".padding:eq(2)").append(output);
                            }
                        } else if (len == 2) {
                            if (i % 2 == 0) {
                                $(".padding:first").append(output);
                            } else if (i % 2 == 1) {
                                $(".padding:last").append(output);
                            }
                        } else if (len == 1) {
                            $(".padding:first").append(output);
                        }
                    }
                }

            }, 500)
        });
    },
    error: function(err) {
        console.log(err);
    }

});